mapper.select:
select 

from exception_journal
where SYS_EVT_TRACE_ID = #{sysEvtTraceId,jdbcType=VARCHAR}
  and TX_TYPE_IND = #{txTypeInd,jdbcType=VARCHAR}
  and SYS_TX_CODE = #{sysTxCode,jdbcType=VARCHAR}

mapper.delete:
delete from exception_journal
where SYS_EVT_TRACE_ID = #{sysEvtTraceId,jdbcType=VARCHAR}
  and TX_TYPE_IND = #{txTypeInd,jdbcType=VARCHAR}
  and SYS_TX_CODE = #{sysTxCode,jdbcType=VARCHAR}
